mapper.select:
select c.recipe_CART_NO,
from Recipes r, Recipes_cart c where r.recipe_NO = c.recipe_NO and c.USER_ID = #{cartDTO.userId}
order by title asc

select
*
from
recipes_cart rc 
where rc.recipe_no = #{recipeNo}
and rc.user_id = #{userId}

mapper.update:
UPDATE recipes
SET hit = hit + 1
WHERE Recipe_no = #{Recipe_no}

mapper.select:
select count(*)
from recipes_cart
where recipe_no = #{recipeNo} 
and user_id = #{userId}
